/* Function: mdlOutputs =======================================================
 *
*/
static void mdlOutputs(SimStruct *S, int_T tid)
{
    real_T  *WorldTime = ssGetOutputPortRealSignal(S,0);
    real_T  *ExecTime  = ssGetOutputPortRealSignal(S,1);
    real_T  *dT        = ssGetOutputPortRealSignal(S,2);
    real_T  *overrun   = ssGetOutputPortRealSignal(S,3);
    real_T  *xD        = ssGetDiscStates(S);
    real_T  *rWork     = ssGetRWork(S);
    int_T   *iWork     = ssGetIWork(S);
    static int i = 100;

    *WorldTime = xD[3];

    /* The other outputs are misused in the MEX version (it is different
     * in TLC) to output internal states
     */
    *ExecTime  = xD[1];
    *dT        = rWork[0];
    *overrun   = xD[2];

    if (i++ == 100) {
        rWork[0] = ssGetT(S)+1.0;
        i = 0;
    }

    if (rWork[0] != rWork[1]) {
        real_T   weight = TIMESTEP_WEIGHT, /* Weighting factor for time jump */
                 w;             /* filters natural freq */

        /* Input time changed */
        if (iWork[0]) {
            iWork[0]--;
            weight = 1.0/(1/TIMESTEP_WEIGHT - iWork[0]);
        }

        /* Make a sliding average of input time steps, weighting the change
         * from 1 to 0.1 for the first 10 changes, thereafter keeping 0.1
         * xD[0] then is the average of the time jumps this system sees.
         */
        xD[0] = (1 - weight)*xD[0] 
            + weight*(rWork[0] - rWork[1]);
        rWork[1] = rWork[0];

        /* Make filter cutoff frequency 1/10th of time jump frequency */
        w = OMEGA/xD[0];
        rWork[2] = 2*DAMPING*w;
        rWork[3] = w*w;

        /* Work out the error of WorldTime - myTime */
        xD[1] = rWork[0] - xD[3];
    }
}

#define MDL_UPDATE
/* Function: mdlUpdate =======================================================
 *
*/
static void mdlUpdate(SimStruct *S, int_T tid)
{
    real_T  *rWork     = ssGetRWork(S);
    real_T  *xD        = ssGetDiscStates(S);

    xD[3] += TSAMPLE*(rWork[2]*xD[1] + xD[2]);
    xD[2] += TSAMPLE*rWork[3]*xD[1];
}

