#define MDL_RTW
static void mdlRTW(SimStruct *S)
{
    real_T Ts = TSAMPLE;
    real_T omega = OMEGA;
    real_T damping = DAMPING;
    real_T weight = TIMESTEP_WEIGHT; /* Weighting factor for time jump */

    if (!ssWriteRTWWorkVect(S, "RWork", 4, 
                "WorldTime", 1,
                "oldWorldTime", 1,
                "k_p", 1,
                "k_i", 1
                ))
        return;
    if (!ssWriteRTWScalarParam(S, "Omega", &omega, SS_DOUBLE))
        return;
    if (!ssWriteRTWScalarParam(S, "Weight", &weight, SS_DOUBLE))
        return;
    if (!ssWriteRTWScalarParam(S, "Damping", &damping, SS_DOUBLE))
        return;
    if (!ssWriteRTWScalarParam(S, "Ts", &Ts, SS_DOUBLE))
        return;


}

